// SideBar.js
import React from "react";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import OrdersList from './OrdersList';
import PizzasList from './PizzasList';
import UsersList from './UsersList';

export default function () {
  return (
    <Router>
      <div style={{margin: '10px'}}>
        <ul className="sidebar navbar-nav">
          <li className="nav-item ">
            <Link to={"/admin"} className="nav-link">
              <i className="fa fa-user" />
              <span>Usera</span>
            </Link>
          </li>
          <li className="nav-item">
            <Link to={"/admin/AboutPage"} className="nav-link">
              <i class="fas fa-pizza-slice" />
              <span>Pizzas</span>
            </Link>
          </li>
          <li className="nav-item">
            <Link to={"/admin/ContactPage"} className="nav-link">
              <i className="fa fa-table" />
              <span>Contact</span>
            </Link>
          </li>
        </ul>
        <Switch>
          <Route exact path="/admin" component={OrdersList} />
          <Route path="/admin/AboutPage" component={PizzasList} />
          <Route path="/admin/ContactPage" component={UsersList} />
        </Switch>
      </div>
    </Router>
  );
}
